import React from 'react';
import { TextField, Button, Container, Typography } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import { Formik } from 'formik';

const useStyles = makeStyles((theme) => ({
	root: {
		'& .MuiTextField-root': {
			margin: theme.spacing(1),
			width: 200,
		},
	},
}));

export const SignUp = () => {
	const classes = useStyles();

	return (
		<Container maxWidth='sm'>
			<Typography
				component='div'
				style={{ display : "flex", flexDirection : "column", backgroundColor: '#cfe8fc', height: '100vh' }}
			>
				<Formik
					initialValues={{ email: '', password: '' }}
					validate={(values) => {
						const errors = {};
						if (!values.email) {
							errors.email = 'Required';
						} else if (
							!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(
								values.email
							)
						) {
							errors.email = 'Invalid email address';
						}
						return errors;
					}}
					onSubmit={(values, { setSubmitting }) => {
						setTimeout(() => {
							alert(JSON.stringify(values, null, 2));
							setSubmitting(false);
						}, 400);
					}}
				>
					{({
						values,
						errors,
						touched,
						handleChange,
						handleBlur,
						handleSubmit,
						isSubmitting,
					}) => (
						<form
							onSubmit={handleSubmit}
							className={classes.root}
							noValidate
							autoComplete='on'
						>
							<div>
								<TextField
									id='email'
									label='email'
									variant='filled'
									onChange={handleChange}
									onBlur={handleBlur}
									helperText={
										errors.email &&
										touched.email &&
										errors.email
									}
								/>

								<TextField
									id='password'
									label='password'
									variant='filled'
									onChange={handleChange}
									onBlur={handleBlur}
									helperText={
										errors.password &&
										touched.password &&
										errors.password
									}
								/>
							</div>
							<div>
								<Button type='submit' variant='contained'>
									Register
								</Button>
								<Button variant='contained'>
									SignIn with google
								</Button>
							</div>
						</form>
					)}
				</Formik>
			</Typography>
		</Container>
	);
};
