import React from 'react';
import { TextField, Button, Grid } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import { Formik } from 'formik';

const useStyles = makeStyles((theme) => ({
	root: {
		'& .MuiTextField-root': {
			margin: theme.spacing(1),
		},
		form: {
			width: '100%',
		},
	},
}));

export const SignUp = () => {
	const styles = useStyles();
	return (
		<Formik
			initialValues={{ email: '', password: '' }}
			validate={(values) => {
				const errors = {};
				if (!values.email) {
					errors.email = 'Required';
				} else if (
					!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(
						values.email
					)
				) {
					errors.email = 'Invalid email address';
				}
				return errors;
			}}
			onSubmit={(values) => {
				setTimeout(() => {
					alert(JSON.stringify(values, null, 2));

				}, 4);
			}}
		>
			{({
				values,
				errors,
				touched,
				handleChange,
				handleBlur,
				handleSubmit,
				isSubmitting,
				/* and other goodies */
			}) => (
				<form
					onSubmit={handleSubmit}
					noValidate
					autoComplete='on'
					className={styles.form}
				>
					<Grid
						container
						direction='column'
						justify='center'
						alignItems='stretch'
						spacing={3}
					>
						<Grid item xs={6}>
							<TextField
								error
								id='email'
								label='Email'
								helperText='Incorrect entry.'
								variant='filled'
								fullWidth
								onChange={handleChange}
								onBlur={handleBlur}


							/>
						</Grid>
						<Grid item xs={6}>
							<TextField
								error
								id='filled-error-helper-text'
								label='Password'
								helperText='Incorrect entry.'
								variant='filled'
								fullWidth
                                onChange={handleChange}
								onBlur={handleBlur}

							/>
						</Grid>
						<Grid item xs={6}>
							<Button
                                disabled={isSubmitting}
                                type='submit' 
                                variant='contained' fullWidth>
								    Register
							</Button>
						</Grid>
						<Grid item xs={6}>
							<Button variant='contained' fullWidth>
								Register with Google
							</Button>
						</Grid>
					</Grid>
				</form>
			)}
		</Formik>
	);
};
